USE sisec;

ALTER TABLE `sisec`.`alumno` 
DROP FOREIGN KEY `fk_alumno_plantel`;
ALTER TABLE `sisec`.`alumno` 
ADD COLUMN `carrera_id` INT(11) NULL AFTER `plantel_id`,
ADD COLUMN `numero_contacto` VARCHAR(10) NULL AFTER `certificado_parcial`,
ADD COLUMN `numero_movil` VARCHAR(10) NULL AFTER `numero_contacto`,
ADD COLUMN `direccion` VARCHAR(100) NULL AFTER `numero_movil`,
ADD COLUMN `codigo_postal` VARCHAR(5) NULL AFTER `direccion`,
ADD COLUMN `cambio_subsistema` TINYINT NOT NULL DEFAULT 0 AFTER `codigo_postal`,
ADD COLUMN `cambio_carrera` TINYINT NOT NULL DEFAULT 0 AFTER `cambio_subsistema`,
ADD COLUMN `estatus` ENUM('Documentos incompletos', 'Documentos completos') NOT NULL DEFAULT 'Documentos Completos' AFTER `cambio_carrera`,
ADD COLUMN `estatus_inscripcion` ENUM('Activo', 'Baja', 'Egresado') NOT NULL DEFAULT 'Egresado' AFTER `estatus`,
ADD COLUMN `permitir_inscripcion` ENUM('Permitir', 'No Permitir') NOT NULL DEFAULT 'Permitir' AFTER `estatus_inscripcion`,
ADD COLUMN `tipo_alumno` ENUM('Regular', 'Irregular') NOT NULL DEFAULT 'Regular' AFTER `permitir_inscripcion`,
ADD COLUMN `tipo_trayectoria` ENUM('Regular', 'Transito') NOT NULL DEFAULT 'Regular' AFTER `tipo_alumno`,
CHANGE COLUMN `periodo_inicio` `periodo_inicio` DATE NULL ,
CHANGE COLUMN `periodo_termino` `periodo_termino` DATE NULL ,
CHANGE COLUMN `generacion` `generacion` VARCHAR(10) NULL ,
CHANGE COLUMN `semestre` `semestre` INT(11) NOT NULL ,
ADD INDEX `fk_alumno_carrera_idx` (`carrera_id` ASC);

ALTER TABLE `sisec`.`alumno` 
